import React from 'react';
import axios from 'axios';

export default class Particulier extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            nom: '',
            prenom: '',
            phone: '',
            email: ''
        }
        this.onChange = this.onChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    onChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    }


    handleSubmit(e)  {
        console.log('tung atu zay oh');
        axios.post('http://localhost:8080/api/users/particulier', e.action.value)
            .then(res => {
                console.log(res);
                console.log(res.data);
            }).catch(function (error) {
                console.log('aty amny erreur zay anh' + error);
            })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit({
                    nom: this.state.nom,
                    prenom: this.state.prenom,
                    email: this.state.email,
                    phone: this.state.phone,
                })}>
                    <div className="form-group mx-sm-3 mb-2 container">
                        <div className="row">
                            <div className="col-xs-6">

                                <input className="form-control" type="text"
                                    value={this.state.value}
                                    onChange={this.onChange}
                                    name="nom" placeholder="Votre nom" />
                            </div>
                        </div>
                        <br />
                        <br />
                        <div className="row">
                            <div className="col-xs-6">

                                <input className="form-control" type="text"
                                    value={this.state.value}
                                    onChange={this.onChange}
                                    name="prenom" placeholder="Votre prenom" />
                            </div>
                        </div>
                        <br />
                        <br />
                        <div className="row">
                            <div className="col-xs-6">

                                <input className="form-control" type="text"
                                    value={this.state.value}
                                    onChange={this.onChange}
                                    name="email" placeholder="Votre email" />
                            </div>
                        </div>
                        <br />
                        <br />
                        <div className="row">
                            <div className="col-xs-6">

                                <input className="form-control" type="text"
                                    value={this.state.value}
                                    onChange={this.onChange}
                                    name="phone" placeholder="Votre Numero" />
                            </div>
                        </div>
                        <br />
                        <br />

                        <button type="submit" id="validate" className="btn btn-info">Valider</button>



                    </div>
                </form>
            </div>
        )
    }
}
