import React from 'react';

class Particulier extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      nom: '',
      prenom: '',
      phone: '',
      email: ''

    };
    this.onChange = this.onChange.bind(this)
    this.handleCreate = this.handleCreate.bind(this);
  }




  onChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    })
  }





  handleCreate(ev) {
    ev.preventDefault();

    const data ={
        nom : this.state.nom,
        prenom : this.state.prenom,
        email : this.state.email,
        phone : this.state.phone,
    }

    fetch('http://localhost:8080/api/users/particulier', {
      method: 'POST',
      body: data,
    }).then((response) => {
        console.log(response.data)
    });
  }


  render() {
    return (
      <div className="container-fluid">
        <form onSubmit={this.handleCreate} className="md-form">
          <div className="form-group mx-sm-3 mb-2 container">
            <div className="row">
              <div className="col-xs-6">

              <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="nom" placeholder="Votre nom" />
              </div>
            </div>
            <br />
            <br />
            <div className="row">
              <div className="col-xs-6">

              <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="prenom" placeholder="Votre prenom" />
              </div>
            </div>
            <br />
            <br />
            <div className="row">
              <div className="col-xs-6">

              <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="email" placeholder="Votre email" />
              </div>
            </div>
            <br />
            <br />
            <div className="row">
              <div className="col-xs-6">

              <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="phone" placeholder="Votre Numero" />
              </div>
            </div>
            <br />
            <br />

                <button id="validate" className="btn btn-info">Publier</button>



            </div>
        </form>
      </div>

    );
  }
}

export default Particulier;
