import React, { Component } from 'react';
import axios from 'axios';

export default class EditAtelier extends Component {
  constructor(props) {
    super(props);
    this.onChange = this.onChange.bind(this)
    this.handleUploadImage = this.handleUploadImage.bind(this);

    this.state = {
        titre: '',
        description: '',
        date: '',
        debut: '',
        duree: '',
        place: '',
        idUser:'',
        prix: '',
        image:'',
    }
  }

  componentDidMount() {
      console.log('parametre'+this.props.match.params.id);

      axios.get('http://localhost:8080/api/users/putArticle/'+this.props.match.params.id)
          .then(response => {
                this.setState({ 
                    titre:response.data.titre,
                    description: response.data.description,
                    debut: response.data.debut,
                    duree: response.data.duree,
                    place: response.data.place,
                    prix: response.data.prix,
                    image: response.data.image,
                    idUser: localStorage.id
                })
            })
          .catch(function (error) {
              console.log(error);
          })
        }

        onChange(event) {
            this.setState({
              [event.target.name]: event.target.value
            })
          }


          handleUploadImage(ev) {
            ev.preventDefault();

            const data = new FormData();
            data.append('image', this.uploadInput.files[0]);
            data.append('titre', this.state.titre);
            data.append('prix', this.state.prix);
            data.append('debut', this.state.debut);
            data.append('duree', this.state.duree);
            data.append('place', this.state.place);
            data.append('idUser', localStorage.id);
            data.append('description', this.state.description)

    axios.post('http://localhost:8080/api/users/putArticle/'+this.props.match.params.id)
        .then((response) => {
    response.json().then((body) => {
      this.setState({ image: `http://localhost:8080/api/users/newArticle/${body.image}` });
      console.log('ity ilay body.image', body.image);

    });
  });

    //this.props.history.push('/');
  }

  render() {
    return (
        <div className="container-fluid">
        <form onSubmit={this.handleUploadImage} className="md-form">
          <div className="form-group mx-sm-3 mb-2 container">
            <div className="row">
              <div className="col-xs-6">

              <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="titre" placeholder="Titre" />

              </div>
              <div className="col-xs-6">

              <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="prix" placeholder="Prix" />

              </div>
            </div>
            <br />
            <br />
            <div className="row">
              <div className="col-xs-6">
              <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="debut" placeholder="Debut" />
              </div>

              <div className="col-xs-6">

                <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="description" placeholder="Description" />

              </div>
            </div>

            <br />
            <div className="row">
              <div className="col-xs-6">
              <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="place" placeholder="Nombre des places" />
              </div>
              <div className="col-xs-6">
                <input className="form-control" type="text"
                  value={this.state.value}
                  onChange={this.onChange}
                  name="duree" placeholder="Durée" />
              </div>
            </div>
            <br />

            <div className="row">
            <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="image" />
                <button id="validate" className="btn btn-info">Publier</button>
            </div>


          </div>

        </form>
      </div>
    )
  }
}
