import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'


export default class ListTout extends Component {

    constructor(props) {
        super(props);
        this.state = { produit: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/api/users/newArticle')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ produit: response.data });
                console.log('i am a produit', this.state.produit)
                localStorage.setItem('loc', response.data[1].place)
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <div>
            <div className="container-fluid">

                {
                    (this.state.produit.length > 0) ? (this.state.produit.map((obj) => {
                        return <div className="container-fluid">
                            <div id="ligne" className="row" key={obj._id}>
                                <div className="container">
                                    <img className="img-fluid " width="50%" height="20%"
                                        src={'http://localhost:8080/api/users/newArticleImage/' + obj.image}
                                        alt="pdp" />
                                </div>
                                <div className="container">
                                    <div className="container-fluid">
                                        <div className="row">
                                            <div className="col-md-3">
                                                <h2>{obj.titre}</h2>
                                            </div>
                                            <div className="col-md-9">
                                                <h3><u>Prix</u> :</h3>
                                                <p>{obj.prix}</p>
                                            </div>
                                        </div>
                                        <div className="row">
                                            <div className="col-md-3">
                                                <h3><u>Durée</u> :</h3>
                                                <p>{obj.duree}</p>
                                            </div>
                                            <div className="col-md-9">
                                                <div className="container-fluid">
                                                    <h3><u>Description</u> :</h3>
                                                    <p>{obj.description}</p>

                                                </div>
                                                <div className="container-fluid">
                                                    <h5>Publier en</h5>
                                                    <p>{obj.date}</p>

                                                </div>

                                            </div>
                                        </div>
                                        <div className="row">
                                            <p>
                                                Les places sont limitées à {obj.place}! <br />
                                                Si vous êtes interessé, s'inscrire c'est obligatoire
                                            </p>
                                        </div>
                                        <div className="row">
                                            <div className="container">
                                                <Link to="/Particulier/" className="btn btn-sm btn-#f3671f">M'INSCRIRE</Link>
                                            </div>
                                        </div>

                                    </div>
                                </div>

                            </div>
                        </div>

                    })) : ('')
                }

            </div>
        </div>
    }
    render() {
        return (
            <div>
                {this.liste()}
            </div>
        );
    }
}
